import React, { useState } from 'react';
import spanishFlag from '../../assets/spainFlag.jpg';
import englishFlag from '../../assets/usaFlag.jpg';

const LanguageButton = ({ onChange }) => {
    const [isChecked, setIsChecked] = useState(false);

    const handleToggle = () => {
        setIsChecked(!isChecked);
        onChange(!isChecked); // Llama a la función onChange con el nuevo estado invertido
    };

    return (
        <div className="toggle-switch-language">
        <button
            className="language-button"
            style={{
                backgroundImage: `url(${isChecked ? englishFlag : spanishFlag})`,
                width: '40px',
                height: '35px',
                backgroundSize: '80% 100%',
                backgroundRepeat: 'no-repeat',
                backgroundPosition: 'center',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
            }}
            onClick={handleToggle}
        />
        </div>
    );
};

export default LanguageButton;
